import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { skipSizes } from '../data/skipSizes';

const SkipSelector = ({ onSkipsSelected }) => {
  const [selectedSkips, setSelectedSkips] = useState(new Set());
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('success');
  const [explodedCards, setExplodedCards] = useState(new Set());
  const [loadedImages, setLoadedImages] = useState(new Set());

  useEffect(() => {
    const timer = setTimeout(() => {
      const newExplodedCards = new Set();
      skipSizes.forEach((skip, index) => {
        setTimeout(() => {
          newExplodedCards.add(skip.size);
          setExplodedCards(new Set(newExplodedCards));
        }, index * 100);
      });
    }, 100);
    return () => clearTimeout(timer);
  }, []);

  const handleImageLoad = (size) => {
    setLoadedImages(prev => new Set(prev).add(size));
  };

  const toggleSkip = (size) => {
    const newSelectedSkips = new Set(selectedSkips);
    if (selectedSkips.has(size)) {
      newSelectedSkips.delete(size);
    } else {
      newSelectedSkips.add(size);
    }
    setSelectedSkips(newSelectedSkips);
  };

  const handleSubmit = () => {
    if (selectedSkips.size === 0) {
      setModalType('error');
      setShowModal(true);
      return;
    }

    const selectedSkipDetails = Array.from(selectedSkips).map(size => {
      const skip = skipSizes.find(s => s.size === size);
      return {
        size: skip.size,
        price: skip.price
      };
    });

    setModalType('success');
    setShowModal(true);
    onSkipsSelected(selectedSkipDetails);
  };

  const getTotalPrice = () => {
    return Array.from(selectedSkips).reduce((total, size) => {
      const skip = skipSizes.find(s => s.size === size);
      return total + parseFloat(skip.price.replace('Â£', ''));
    }, 0);
  };

  return (
    <div className="relative min-h-screen p-8 bg-gradient-to-b from-gray-900 via-blue-900 to-gray-900">
      <style jsx global>{`
        @keyframes explodeIn {
          0% {
            opacity: 0;
            transform: scale(0) rotate(-15deg);
          }
          70% {
            transform: scale(1.1) rotate(5deg);
          }
          100% {
            opacity: 1;
            transform: scale(1) rotate(0);
          }
        }

        @keyframes emergeFromDepth {
          0% {
            transform: translateZ(-1000px) rotateX(45deg);
            opacity: 0;
          }
          100% {
            transform: translateZ(0) rotateX(0);
            opacity: 1;
          }
        }

        @keyframes glowPulse {
          0%, 100% {
            box-shadow: 0 0 20px rgba(34, 197, 94, 0.6),
                        0 0 40px rgba(34, 197, 94, 0.4),
                        0 0 60px rgba(34, 197, 94, 0.2);
          }
          50% {
            box-shadow: 0 0 30px rgba(34, 197, 94, 0.7),
                        0 0 50px rgba(34, 197, 94, 0.5),
                        0 0 70px rgba(34, 197, 94, 0.3);
          }
        }

        @keyframes blueGlowPulse {
          0%, 100% {
            box-shadow: 0 0 20px rgba(59, 130, 246, 0.6),
                        0 0 40px rgba(59, 130, 246, 0.4),
                        0 0 60px rgba(59, 130, 246, 0.2);
          }
          50% {
            box-shadow: 0 0 30px rgba(59, 130, 246, 0.7),
                        0 0 50px rgba(59, 130, 246, 0.5),
                        0 0 70px rgba(59, 130, 246, 0.3);
          }
        }

        .skip-box {
          transition: all 0.3s ease-in-out;
        }

        .skip-box:hover {
          box-shadow: 0 0 25px rgba(59, 130, 246, 0.7),
                     0 0 45px rgba(59, 130, 246, 0.5),
                     0 0 65px rgba(59, 130, 246, 0.3);
          transform: translateY(-5px);
        }

        .image-hover-glow {
          transition: all 0.3s ease-in-out;
        }

        .image-hover-glow:hover {
          filter: drop-shadow(0 0 15px rgba(59, 130, 246, 0.5))
                 drop-shadow(0 0 30px rgba(59, 130, 246, 0.3));
          transform: scale(1.05);
        }

        .image-hover-glow-selected:hover {
          filter: drop-shadow(0 0 15px rgba(34, 197, 94, 0.5))
                 drop-shadow(0 0 30px rgba(34, 197, 94, 0.3));
          transform: scale(1.05);
        }
      `}</style>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-7xl mx-auto">
        {skipSizes.map((skip, index) => (
          <div
            key={skip.size}
            onClick={() => toggleSkip(skip.size)}
            className={`
              skip-box
              relative overflow-hidden rounded-2xl shadow-xl 
              transition-all duration-300 cursor-pointer
              hover:shadow-2xl hover:scale-[1.02]
              min-h-[600px]
              ${explodedCards.has(skip.size)
                ? 'animate-[explodeIn_0.6s_ease-out_forwards]'
                : 'opacity-0 scale-0'
              }
              ${selectedSkips.has(skip.size)
                ? 'bg-gradient-to-br from-green-900 to-green-800 animate-[glowPulse_2s_ease-in-out_infinite]'
                : 'bg-gradient-to-br from-gray-900 to-gray-800 animate-[blueGlowPulse_2s_ease-in-out_infinite]'
              }
            `}
            style={{
              animationDelay: `${index * 100}ms`
            }}
          >
            <div className="absolute top-4 right-4 z-10">
              <div className={`px-4 py-2 rounded-full shadow-lg transition-all duration-300 ${
                selectedSkips.has(skip.size)
                  ? 'bg-gradient-to-r from-green-600 to-green-400'
                  : 'bg-gradient-to-r from-blue-600 to-blue-400'
              }`}>
                <span className="text-white font-bold text-lg">{skip.price}</span>
              </div>
            </div>

            <div className="relative flex items-center justify-center h-80 bg-gradient-to-b from-transparent to-transparent p-6 perspective-1000">
              <div className={`relative w-[90%] h-[90%] ${
                loadedImages.has(skip.size) 
                  ? 'animate-[emergeFromDepth_1.5s_ease-out_forwards]'
                  : 'opacity-0'
              }`}>
                <img
                  src={skip.size >= 20 ? '/images/yellow-skip.png' : '/images/green-skip.png'}
                  alt={`${skip.size} Yard Skip`}
                  className={`
                    object-contain w-full h-full 
                    transition-all duration-500
                    ${selectedSkips.has(skip.size) ? 'image-hover-glow-selected' : 'image-hover-glow'}
                  `}
                  onLoad={() => handleImageLoad(skip.size)}
                />
              </div>
              <div className={`absolute top-2 left-2 px-3 py-1 rounded-full text-sm font-semibold transition-all duration-300 ${
                selectedSkips.has(skip.size)
                  ? 'bg-green-500 text-white'
                  : 'bg-blue-500 text-white'
              }`}>
                {skip.size} Yards
              </div>
              {selectedSkips.has(skip.size) && (
                <div className="absolute top-2 right-16 bg-green-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
                  Selected
                </div>
              )}
            </div>

            <div className="p-6 pt-4">
              <h3 className="text-xl font-bold text-white mb-2">
                {skip.size} Yard Skip
              </h3>
              <p className={`text-sm mb-3 transition-colors duration-300 ${
                selectedSkips.has(skip.size) ? 'text-green-300' : 'text-gray-400'
              }`}>
                {skip.hirePeriod}
              </p>
              <p className={`text-sm leading-relaxed transition-colors duration-300 ${
                selectedSkips.has(skip.size) ? 'text-green-200' : 'text-gray-400'
              }`}>
                {skip.description}
              </p>
            </div>
          </div>
        ))}
      </div>

      <motion.div
        className="fixed bottom-8 right-8 z-50"
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ delay: 1, type: "spring", stiffness: 260, damping: 20 }}
      >
        <button
          onClick={handleSubmit}
          className="bg-gradient-to-r from-blue-600 to-blue-400 hover:from-blue-700 hover:to-blue-500 
                   text-white font-bold py-4 px-8 rounded-full shadow-lg 
                   transform transition-all duration-300 hover:scale-105
                   flex items-center justify-center space-x-2"
        >
          <span>Continue with Selection</span>
          {selectedSkips.size > 0 && (
            <span className="bg-white text-blue-600 rounded-full w-6 h-6 flex items-center justify-center text-sm">
              {selectedSkips.size}
            </span>
          )}
        </button>
      </motion.div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white bg-opacity-10 backdrop-blur-lg p-8 rounded-2xl max-w-md w-full mx-4 shadow-2xl border border-white border-opacity-20">
            <h2 className={`text-2xl font-bold mb-4 ${modalType === 'success' ? 'text-green-400' : 'text-red-400'}`}>
              {modalType === 'success' ? 'Selection Confirmed!' : 'Error'}
            </h2>
            {modalType === 'success' ? (
              <div>
                <p className="text-white mb-4">You have selected the following skips:</p>
                <ul className="mb-4">
                  {Array.from(selectedSkips).map(size => {
                    const skip = skipSizes.find(s => s.size === size);
                    return (
                      <li key={size} className="text-white flex justify-between mb-2">
                        <span>{size} Yard Skip</span>
                        <span>{skip.price}</span>
                      </li>
                    );
                  })}
                  <li className="text-white flex justify-between mt-4 pt-4 border-t border-white border-opacity-20 font-bold">
                    <span>Total</span>
                    <span>Â£{getTotalPrice().toFixed(2)}</span>
                  </li>
                </ul>
              </div>
            ) : (
              <p className="text-white mb-4">Please select at least one skip to continue.</p>
            )}
            <button
              onClick={() => setShowModal(false)}
              className={`w-full py-2 px-4 rounded-lg font-semibold transition-colors duration-300 
                ${modalType === 'success' 
                  ? 'bg-green-500 hover:bg-green-600 text-white'
                  : 'bg-red-500 hover:bg-red-600 text-white'}`}
            >
              {modalType === 'success' ? 'Close' : 'Try Again'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SkipSelector;
