import React, { useState } from 'react';
import SkipSelector from './components/SkipSelector';

const RemWastePage = () => {
  const [selectedSize, setSelectedSize] = useState(null);

  const handleSkipSelect = (size) => {
    setSelectedSize(size);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">
            REM Waste Skip Selector
          </h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-white rounded-lg shadow">
            <div className="p-6">
              <h2 className="text-2xl font-semibold mb-4">
                Select Your Skip Size
              </h2>
              <SkipSelector
                onSelect={handleSkipSelect}
                selectedSize={selectedSize}
              />
            </div>
            {selectedSize && (
              <div className="border-t border-gray-200 p-6">
                <h3 className="text-lg font-medium text-gray-900">
                  Selected Skip Size: {selectedSize} Yards
                </h3>
                <p className="mt-2 text-gray-600">
                  {selectedSize >= 20
                    ? 'Large capacity skip suitable for major projects and commercial use'
                    : 'Standard skip perfect for domestic and smaller commercial projects'}
                </p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default RemWastePage;
