import React from 'react';

const skipSizes = [
  { size: 4, type: 'green', price: '£211', hirePeriod: '14 day hire period' },
  { size: 5, type: 'green', price: '£241', hirePeriod: '14 day hire period' },
  { size: 6, type: 'green', price: '£284', hirePeriod: '14 day hire period' },
  { size: 8, type: 'green', price: '£295', hirePeriod: '14 day hire period' },
  { size: 10, type: 'green', price: '£356', hirePeriod: '14 day hire period' },
  { size: 12, type: 'green', price: '£380', hirePeriod: '14 day hire period' },
  { size: 14, type: 'green', price: '£434', hirePeriod: '14 day hire period' },
  { size: 16, type: 'green', price: '£510', hirePeriod: '7 day hire period' },
  { size: 18, type: 'green', price: '£650', hirePeriod: '14 day hire period' },
  { size: 20, type: 'yellow', price: '£802', hirePeriod: '14 day hire period' },
  { size: 40, type: 'yellow', price: '£877', hirePeriod: '14 day hire period' },
];

const SkipSelector = ({ onSelect, selectedSize }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 p-6">
      {skipSizes.map((skip) => (
        <div
          key={skip.size}
          className={`relative overflow-hidden rounded-2xl bg-gradient-to-br from-gray-900 to-gray-800 shadow-xl transition-all duration-300 hover:shadow-2xl hover:scale-[1.02] ${
            selectedSize === skip.size
              ? 'ring-4 ring-blue-500 ring-opacity-50'
              : ''
          }`}
        >
          {/* Price Tag */}
          <div className="absolute top-4 right-4 z-10">
            <div className="bg-gradient-to-r from-blue-600 to-blue-400 px-4 py-2 rounded-full shadow-lg">
              <span className="text-white font-bold text-lg">{skip.price}</span>
            </div>
          </div>

          {/* Skip Image Container */}
          <div className="relative aspect-w-16 aspect-h-9 bg-gradient-to-b from-gray-800 to-transparent p-6">
            <img
              src={`/images/${skip.type}-skip.png`}
              alt={`${skip.size} Yard Skip`}
              className="object-contain w-full h-full transform transition-transform duration-500 hover:scale-105"
            />
            <div className="absolute top-2 left-2 bg-blue-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
              {skip.size} Yards
            </div>
          </div>

          {/* Content */}
          <div className="p-6 pt-4">
            <h3 className="text-xl font-bold text-white mb-2">
              {skip.size} Yard Skip
            </h3>
            <p className="text-gray-400 text-sm mb-4">{skip.hirePeriod}</p>
            
            {/* Animated Button */}
            <button
              onClick={() => onSelect(skip.size)}
              className="w-full group relative inline-flex items-center justify-center px-6 py-3 text-lg font-medium text-white transition-all duration-300 ease-in-out rounded-lg overflow-hidden bg-gradient-to-r from-blue-600 to-blue-400 hover:from-blue-500 hover:to-blue-300"
            >
              <span className="absolute inset-0 w-full h-full bg-gradient-to-br from-blue-600 to-blue-400 opacity-0 group-hover:opacity-100 transition-opacity duration-300 ease-in-out transform scale-x-0 group-hover:scale-x-100 origin-left"></span>
              <span className="relative flex items-center justify-center gap-2">
                Choose This Skip
                <svg 
                  className="w-5 h-5 transform transition-transform duration-300 group-hover:translate-x-1" 
                  fill="none" 
                  viewBox="0 0 24 24" 
                  stroke="currentColor"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M13 7l5 5m0 0l-5 5m5-5H6" 
                  />
                </svg>
              </span>
            </button>
          </div>

          {/* Not Allowed Overlay - if needed */}
          {skip.notAllowed && (
            <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
              <span className="text-white bg-red-500 px-4 py-2 rounded-lg">
                Not Allowed On This Road
              </span>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default SkipSelector;
