import React, { useState, useEffect } from 'react';

const skipSizes = [
  { 
    size: 4, 
    type: 'green', 
    price: '£211', 
    hirePeriod: '14 day hire period',
    description: 'Perfect for small home renovations and garden clearance. Ideal for kitchen or bathroom refits and small amounts of soil or rubble.'
  },
  { 
    size: 5, 
    type: 'green', 
    price: '£241', 
    hirePeriod: '14 day hire period',
    description: 'Suitable for medium-sized home improvement projects. Great for house clearances and moderate garden waste disposal.'
  },
  { 
    size: 6, 
    type: 'green', 
    price: '£284', 
    hirePeriod: '14 day hire period',
    description: 'Popular choice for home renovations. Ideal for larger garden projects and household waste clearance.'
  },
  { 
    size: 8, 
    type: 'green', 
    price: '£295', 
    hirePeriod: '14 day hire period',
    description: 'Perfect for major home renovations. Suitable for heavy materials like soil, rubble, and concrete.'
  },
  { 
    size: 10, 
    type: 'green', 
    price: '£356', 
    hirePeriod: '14 day hire period',
    description: 'Ideal for commercial projects and large home renovations. Great for bulky waste and construction materials.'
  },
  { 
    size: 12, 
    type: 'green', 
    price: '£380', 
    hirePeriod: '14 day hire period',
    description: 'Excellent for substantial construction work. Perfect for large-scale garden landscaping and building projects.'
  },
  { 
    size: 14, 
    type: 'green', 
    price: '£434', 
    hirePeriod: '14 day hire period',
    description: 'Designed for major construction projects. Ideal for commercial waste and large-scale site clearances.'
  },
  { 
    size: 16, 
    type: 'green', 
    price: '£510', 
    hirePeriod: '7 day hire period',
    description: 'Suitable for industrial use and major construction. Perfect for large volume waste removal and site clearance.'
  },
  { 
    size: 18, 
    type: 'green', 
    price: '£650', 
    hirePeriod: '14 day hire period',
    description: 'High-capacity skip for major projects. Ideal for industrial waste and large commercial clearances.'
  },
  { 
    size: 20, 
    type: 'yellow', 
    price: '£802', 
    hirePeriod: '14 day hire period',
    description: 'Industrial-grade skip for maximum capacity. Perfect for large-scale demolition and construction projects.'
  },
  { 
    size: 40, 
    type: 'yellow', 
    price: '£877', 
    hirePeriod: '14 day hire period',
    description: 'Our largest skip for major industrial use. Ideal for extensive demolition work and site clearances.'
  },
];

const SkipSelector = ({ onSkipSelect }) => {
  const [selectedSkips, setSelectedSkips] = useState(new Set());
  const [loadedImages, setLoadedImages] = useState(new Set());
  const [explodedCards, setExplodedCards] = useState(new Set());

  useEffect(() => {
    // Trigger explosion effect for cards with a stagger
    skipSizes.forEach((skip, index) => {
      setTimeout(() => {
        setExplodedCards(prev => new Set([...prev, skip.size]));
      }, index * 100); // 100ms delay between each card
    });
  }, []);

  const toggleSkip = (size) => {
    const newSelected = new Set(selectedSkips);
    if (newSelected.has(size)) {
      newSelected.delete(size);
    } else {
      newSelected.add(size);
    }
    setSelectedSkips(newSelected);
    onSkipSelect(newSelected);
  };

  const handleImageLoad = (size) => {
    setLoadedImages(prev => new Set([...prev, size]));
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-8 p-6">
      <style jsx>{`
        @keyframes emergeFromDepth {
          0% {
            transform: perspective(1000px) translateZ(-1000px) scale(0.5);
            opacity: 0;
          }
          100% {
            transform: perspective(1000px) translateZ(0) scale(1);
            opacity: 1;
          }
        }

        @keyframes explodeIn {
          0% {
            transform: scale(0) rotate(-15deg);
            opacity: 0;
          }
          60% {
            transform: scale(1.2) rotate(5deg);
          }
          80% {
            transform: scale(0.9) rotate(-2deg);
          }
          100% {
            transform: scale(1) rotate(0);
            opacity: 1;
          }
        }

        @keyframes glowPulse {
          0%, 100% {
            box-shadow: 0 0 15px rgba(34, 197, 94, 0.4),
                        0 0 30px rgba(34, 197, 94, 0.2);
          }
          50% {
            box-shadow: 0 0 20px rgba(34, 197, 94, 0.5),
                        0 0 40px rgba(34, 197, 94, 0.3);
          }
        }

        @keyframes blueGlowPulse {
          0%, 100% {
            box-shadow: 0 0 20px rgba(59, 130, 246, 0.6),
                        0 0 40px rgba(59, 130, 246, 0.4),
                        0 0 60px rgba(59, 130, 246, 0.2);
          }
          50% {
            box-shadow: 0 0 30px rgba(59, 130, 246, 0.7),
                        0 0 50px rgba(59, 130, 246, 0.5),
                        0 0 70px rgba(59, 130, 246, 0.3);
          }
        }

        .image-hover-glow {
          transition: all 0.3s ease-in-out;
        }

        .image-hover-glow:hover {
          filter: drop-shadow(0 0 15px rgba(59, 130, 246, 0.5))
                 drop-shadow(0 0 30px rgba(59, 130, 246, 0.3));
          transform: scale(1.05);
        }

        .image-hover-glow-selected:hover {
          filter: drop-shadow(0 0 15px rgba(34, 197, 94, 0.5))
                 drop-shadow(0 0 30px rgba(34, 197, 94, 0.3));
          transform: scale(1.05);
        }
      `}</style>
      {skipSizes.map((skip, index) => (
        <div
          key={skip.size}
          onClick={() => toggleSkip(skip.size)}
          className={`
            relative overflow-hidden rounded-2xl shadow-xl 
            transition-all duration-300 cursor-pointer
            hover:shadow-2xl hover:scale-[1.02]
            min-h-[600px]
            ${explodedCards.has(skip.size)
              ? 'animate-[explodeIn_0.6s_ease-out_forwards]'
              : 'opacity-0 scale-0'
            }
            ${selectedSkips.has(skip.size)
              ? 'bg-gradient-to-br from-green-900 to-green-800 animate-[glowPulse_2s_ease-in-out_infinite]'
              : 'bg-gradient-to-br from-gray-900 to-gray-800 animate-[blueGlowPulse_2s_ease-in-out_infinite]'
            }
          `}
          style={{
            animationDelay: `${index * 100}ms`
          }}
        >
          {/* Price Tag */}
          <div className="absolute top-4 right-4 z-10">
            <div className={`px-4 py-2 rounded-full shadow-lg transition-all duration-300 ${
              selectedSkips.has(skip.size)
                ? 'bg-gradient-to-r from-green-600 to-green-400'
                : 'bg-gradient-to-r from-blue-600 to-blue-400'
            }`}>
              <span className="text-white font-bold text-lg">{skip.price}</span>
            </div>
          </div>

          {/* Skip Image Container */}
          <div className="relative flex items-center justify-center h-80 bg-gradient-to-b from-transparent to-transparent p-6 perspective-1000">
            <div className={`relative w-[90%] h-[90%] ${
              loadedImages.has(skip.size) 
                ? 'animate-[emergeFromDepth_1.5s_ease-out_forwards]'
                : 'opacity-0'
            }`}>
              <img
                src={skip.size >= 20 ? '/images/yellow-skip.png' : '/images/green-skip.png'}
                alt={`${skip.size} Yard Skip`}
                className={`
                  object-contain w-full h-full 
                  transition-all duration-500
                  ${selectedSkips.has(skip.size) ? 'image-hover-glow-selected' : 'image-hover-glow'}
                `}
                onLoad={() => handleImageLoad(skip.size)}
              />
            </div>
            <div className={`absolute top-2 left-2 px-3 py-1 rounded-full text-sm font-semibold transition-all duration-300 ${
              selectedSkips.has(skip.size)
                ? 'bg-green-500 text-white'
                : 'bg-blue-500 text-white'
            }`}>
              {skip.size} Yards
            </div>
            {selectedSkips.has(skip.size) && (
              <div className="absolute top-2 right-16 bg-green-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
                Selected
              </div>
            )}
          </div>

          {/* Content */}
          <div className="p-6 pt-4">
            <h3 className="text-xl font-bold text-white mb-2">
              {skip.size} Yard Skip
            </h3>
            <p className={`text-sm mb-3 transition-colors duration-300 ${
              selectedSkips.has(skip.size) ? 'text-green-300' : 'text-gray-400'
            }`}>
              {skip.hirePeriod}
            </p>
            <p className={`text-sm leading-relaxed transition-colors duration-300 ${
              selectedSkips.has(skip.size) ? 'text-green-200' : 'text-gray-400'
            }`}>
              {skip.description}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default SkipSelector;
